index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from "react";

import { TreeViewComponent } from '@syncfusion/ej2-react-navigations';

import * as dataSource from './dataSource/icons-data.json';
const Icons = () => {
    const data = dataSource;
    const fields = { dataSource: data.iconData, id: 'nodeId', text: 'nodeText', child: 'nodeChild', iconCss: 'icon', imageUrl: 'image' };
    return (<div className='control-pane'>
      <div className='control-section'>
        <div className='control_wrapper'>
          {/* Render the TreeView with image icons */}
          <TreeViewComponent id="treeview" fields={fields} sortOrder='Ascending'/>
        </div>
      </div>
    </div>);
};
export default Icons;

const root = createRoot(document.getElementById('sample'));
root.render(<Icons />);
