index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from "react";

import { TreeViewComponent } from '@syncfusion/ej2-react-navigations';

import * as dataSource from './dataSource/template-data.json';
const Template = () => {
    const data = dataSource;
    const fields = { dataSource: data.templateData, id: 'id', parentID: 'pid', text: 'name', hasChildren: 'hasChild' };
    const cssClass = "template-tree";
    const nodeTemplate = (data) => {
        return (<div>
        <div className="treeviewdiv">
          <div className="textcontent">
            <span className="treeName">{data.name}</span>
          </div>
          {data.count &&
                <div className="countcontainer">
              <span className="treeCount e-badge e-badge-primary">{data.count}</span>
            </div>}
        </div>
      </div>);
    };
    return (<div className='control-pane'>
      <div className='control-section'>
        <div className='tree-control-wrapper'>
          {/* Render the TreeView using template option */}
          <TreeViewComponent fields={fields} nodeTemplate={nodeTemplate} cssClass={cssClass}/>
        </div>
      </div>
    </div>);
};
export default Template;

const root = createRoot(document.getElementById('sample'));
root.render(<Template />);
