styles/Grid/style.css:
@font-face {
    font-family: 'Grid control Font';
    src:
    url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
    }

    [class^="sf-grid-icon-"], [class*=" sf-grid-icon-"] {
     font-family: 'Grid control Font' !important;
    speak: none;
    font-size: 55px;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    }

    .sf-grid-icon-Male:before { content: "\\e700"; }
    .sf-grid-icon-Female:before { content: "\\e701"; }
    .sf-grid-icon-Location:before { content: "\\e702"; }

    .fabric .e-grid-empimg {
        background-color: #0078d6;
    }

    .material .e-grid-empimg, .material3 .e-grid-empimg {
        background-color: #e3165b;
    }

    .highcontrast .e-grid-empimg {
        background-color: #ffd939;
    }

    _:-ms-fullscreen,
    :root .highcontrast .e-userimg.sf-grid-icon-Male::before,
    _:-ms-fullscreen,
    :root .highcontrast .e-userimg.sf-grid-icon-FeMale::before {
        color: black;
    }

    .highcontrast .e-userimg.sf-grid-icon-Male::before,
    .highcontrast .e-userimg.sf-grid-icon-FeMale::before {
        color: black;
    }

    .bootstrap .e-grid-empimg {
        background-color: #0078d6;
    }

    .bootstrap4 .e-grid-empimg,
    .bootstrap5 .e-grid-empimg {
        background-color: #0078d6;
    }

    .tailwind .e-grid-empimg {
        background-color: #4f46e5;
    }

    .fluent .e-grid-empimg {
        background-color: #106EBE;
    }

    #Emptext {
        top: -12px;
        left: 41px;
        position: relative;
    }

    .e-grid-empimg {
        height: 27px;
        width: 29px;
        border-radius: 14px;
        background-color:#e3165b;
    }

    .tailwind3 .e-grid-empimg,
    .tailwind3-dark .e-grid-empimg {
        background-color: rgba(79, 70, 229);
    }

    #overviewgrid.e-rtl #Emptext{
    top: -12px;
    left: -41px;
    position: relative;
    }

    .e-userimg.sf-grid-icon-Male::before,
    .e-userimg.sf-grid-icon-FeMale::before {
        font-size: 13px;
    }

    .e-userimg.sf-grid-icon-Male, .e-userimg.sf-grid-icon-FeMale {  
        margin: 0px 8px;
        line-height: 26px;
        color: white; 
        font-size: 13px;
    }

    .e-userimg.sf-grid-icon-Male::before {
        content: '\\e700';
    }

    .e-userimg.sf-grid-icon-FeMale::before {
        content: '\\e701';
    }

    .highcontrast #overviewgrid td.e-rowcell.e-selectionbackground.e-active,
    .fluent2-highcontrast #overviewgrid td.e-rowcell.e-selectionbackground.e-active {
        background-color: transparent;
        color: white;
    }

    .e-bigger #overviewgrid .statustxt {
        position: relative;
        top: -2px;
    }

     #overviewgrid td.e-rowcell.e-selectionbackground.e-active {
        background-color: transparent;
    }

     #overviewgrid .e-image {
        height: 13px;
        width: 14px;
        margin-right: 0;
    }

    #overviewgrid td.e-rowcell .statustxt.e-activecolor {
        color: #00cc00;
        position: relative;
        top: 9px;
    }

    .bootstrap5 td.e-rowcell .statustxt.e-inactivecolor,
    .bootstrap5 td.e-rowcell .statustxt.e-activecolor {
      top: 8px;
    }

    .bootstrap5 .e-checkboxfiltertext .statustemp .statustxt.e-activecolor,
    .bootstrap5 .e-checkboxfiltertext .statustemp .statustxt.e-inactivecolor {
      position: relative;
      top: 1.5px;
    }

    .e-bigger.bootstrap5 .e-checkboxfiltertext .statustemp .statustxt.e-activecolor,
    .e-bigger.bootstrap5 .e-checkboxfiltertext .statustemp .statustxt.e-inactivecolor {
      top: -0.5px;
    }

    .bootstrap5 .e-checkboxfiltertext #Trusttext {
      margin-left: -4px;
    }

     #overviewgrid td.e-rowcell .statustxt.e-inactivecolor {
        color: #e60000;
        position: relative;
        top: 9px;
    }

    .statustemp.e-inactivecolor {
        background-color: #ffd7cc;
        width: 64px;
    }

    .statustemp.e-activecolor {
        background-color: #ccffcc;
        width: 57px;
    }

    .statustxt.e-activecolor {
        color: #00cc00;
    }

    .statustxt.e-inactivecolor {
        color: #e60000;
    }

    .statustemp {
        position: relative;
        height: 19px;
        border-radius: 5px;
        text-align: center
    }

    .highcontrast #msg {
        color: #FFFFFF
    }

    #msg {
        font-family: "Segoe UI", "GeezaPro", "DejaVu Serif", sans-serif;
        font-size: 14px;
        font-weight: 400;
        opacity: 0.95;
        color: #333;
        float: right;
        margin-top: 3px;
    }

    .fabric-dark #msg, .bootstrap-dark #msg, .tailwind-dark #msg, .bootstrap5-dark #msg, .material-dark #msg, .material3-dark #msg, .fluent-dark #msg, .fluent2-dark #msg, .fluent2-highcontrast #msg {
        color: #ffffff !important;
    }

    @media only screen and (max-width: 370px) {
        #msg {
            float: left;
            margin-top: 3px;
        }
        #overviewgrid.e-default {
            margin-top: 36px;
        }
    }

    #samplecontainer {
        padding-top: 5px;
    }

    #myProgress {
        position: relative;
        height: 18px;
        width: 10em;
        text-align: left;
        background-color: white;
    }

    .highcontrast #overviewgrid #myProgress {
        background-color: black;
    }

    #myBar {
        position: absolute;
        width: 10%;
        height: 100%;
        background-color: #00b300;
    }

    #myBar.progressdisable {
        background-color: #df2222;
    }

    #pbarlabel {
        position: relative;
        left: 10px;
        line-height: 18px;
        font-size: 11px;
        color: white;
    }

    .custom-rating.e-rating-container .e-rating-item-container ,
    .e-bigger .custom-rating.e-rating-container .e-rating-item-container .e-rating-icon,
    .e-bigger.custom-rating.e-rating-container .e-rating-item-container .e-rating-icon {
        font-size: 11px;
    }

    .custom-rating.e-rating-container .e-rating-item-container .e-rating-icon {
        -webkit-text-fill-color: lightgrey;
        -webkit-text-stroke: 1px lightgrey;
    }

    .custom-rating.e-rating-container .e-rating-item-container.e-rating-selected .e-rating-icon {
        background: linear-gradient(to right, #ffa600 var(--rating-value), #ffa600 var(--rating-value));
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        -webkit-text-stroke: 1px #ffa600;
    }

    @-moz-document url-prefix() {     
        #overviewgrid.e-grid.e-grid-min-height .e-rowcell {       
            line-height: 0!important;
    } }
    @-moz-document url-prefix() {    
        #overviewgrid.e-grid.e-grid-min-height .e-rowcell {         
            line-height: 0!important;
    } }
